%pig

data = load '/root/dataset/listings.csv' using PigStorage(',') as (id:int,name:chararray,host_id:chararray,host_name:chararray,neighbourhood_group:chararray,neighbourhood:chararray,latitude:float,longitude:float,room_type:chararray,price:int);

describe data;

%pig.query

pvt_rent = filter data by room_type == 'Private room';
final = group pvt_rent by neighbourhood_group;

foreach final generate group as NeighbourhoodGroup, AVG(pvt_rent.price) as AveragePrice;

%pig.query

pvt_rent2 = filter data by room_type == 'Private room';

final2 = group pvt_rent2 by neighbourhood;
test = foreach final2 generate group as TP10_Neighbourhood, AVG(pvt_rent2.price) as AveragePrice;

sorted = limit test 10;
order sorted by AveragePrice desc;
